using DefaultNamespace;namespace Library.Combate;public class Jugador{    private string name;    private List<Pokemon> ListaPokemons;    private Pokemon pokemonEnTurno;    private Pokedex pokedex;    private bool teamIsAlive;    public Jugador(string name)    {        this.name = name;        this.ListaPokemons = new List<Pokemon>();        this.teamIsAlive = true;    }    public void Asociate(Pokedex pokedex)    {        this.pokedex = pokedex;    }    public bool TeamIsAlive()    {        return teamIsAlive;    }    public string GetName()    {        return this.name;    }    public List<Pokemon> GetPokemons()    {        return ListaPokemons;    }    public Pokemon GetPokemonEnTurno()    {        return pokemonEnTurno;    }        public void AgregarAlEquipo(string nombre)    {                if (ListaPokemons.Count < 6)        {            Pokemon pokemonencontrado = pokedex.EntregarPokemon(nombre);            if (pokemonencontrado != null)            {                                ListaPokemons.Add(pokemonencontrado);                if (ListaPokemons.Count == 1)                {                    pokemonEnTurno = pokemonencontrado;                }                Console.WriteLine($"Se añadió el pokemon {pokemonencontrado.GetName()} a tu equipo, ¿vas a seguir añadiendo más?");            }        }        else        {            Console.WriteLine("Ya tienes 6 Pokemons!");        }    }    public void CambiarPokemon(Pokemon pokemon)    {        if (ListaPokemons.Contains(pokemon))        {            int indicepokemonAremplazar = ListaPokemons.IndexOf(pokemon);            Pokemon pokemonAnterior = ListaPokemons[0];            ListaPokemons[0] = pokemon;            ListaPokemons[indicepokemonAremplazar] = pokemonAnterior;            pokemonEnTurno = pokemon;        }    }        public void MostarEstadoEquipo()    {        Console.WriteLine($"El esta del equipo de {name} es:");        foreach (Pokemon pokemon in ListaPokemons)        {            if (pokemon.GetIsAlive())            {                Console.WriteLine($"{pokemon.GetName()} {pokemon.GetVidaActual()}/{pokemon.GetVidaTotal()}");            }            else            {                Console.WriteLine($"{pokemon.GetName()} ha muerto");            }        }    }}